$(document).ready(function(){"use strict";const externalLinks=function(){const internalHost=[location.hostname];const protocolPattern=/^https?:\/\//i;$("a").each(function(){const href=$(this).attr("href");if(protocolPattern.test(href)){const hrefHostname=$(new URL(href)).attr("hostname");if($.inArray(hrefHostname,internalHost)===-1){$(this).attr("target","_blank");$(this).attr("rel","noopener noreferer")}}})};const renderClock=function(element){const date=new Date;const hour=String(date.getUTCHours()).padStart(2,"0");const minute=String(date.getUTCMinutes()).padStart(2,"0");const second=String(date.getUTCSeconds()).padStart(2,"0");element.html(hour+":"+minute+":"+second);setTimeout(function(){renderClock(element)},500)};$(document).ajaxSuccess(function(){externalLinks()});const init=function(){externalLinks();renderClock($(".eve-time-wrapper .eve-time-clock"));hljs.highlightAll()};init()});